package com.pm.products.controller;import com.pm.products.model.Product;import com.pm.products.service.ProductService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/products")public class ProductController {    @Autowired    private ProductService productService;    @PostMapping    public Product addProduct(@RequestBody Product product) {        return productService.addProduct(product);    }    @GetMapping    public List<Product> getAllProducts() {        return productService.getAllProducts();    }    @GetMapping("/{productId}")    public Product getProductById(@PathVariable int productId) {        return productService.getProductById(productId);    }}